INSERT INTO `labels` VALUES ('1','71','Estee Lauder Lip Jewels' );
INSERT INTO `labels` VALUES ('1','5071','With an elegant design of gold, this is a collection of the three of the best-selling lip colors with each case equipped with a mirror for convenience.' );
INSERT INTO `labels` VALUES ('1','72','Estee Lauder Compact' );
