INSERT INTO `labels` VALUES ('1','78','Lancome Renergie Lift' );
INSERT INTO `labels` VALUES ('1','5078','Utilizing the revolutionary GF-Volumetry technology for rejuvenation, Renergie Lift Volumetry Cream from Lancome vitalizes skin cells, which is crucial in restoring skin elasticity, thereby restoring youthful skin and improved contours of the face.' );
INSERT INTO `labels` VALUES ('1','79','La Mer The Eye Balm' );
INSERT INTO `labels` VALUES ('1','5079','Active yet comforting, this fast-absorbing balm is formulated with the Marine DePuff FermentTM to soothe and reduce visible puffiness as it diminishes the look of lines and wrinkles and visibly lifts and firms the skin.' );
INSERT INTO `labels` VALUES ('1','80','Lancome Rouge' );
