INSERT INTO `labels` VALUES ('1','83','Anna Sui Mini Rouge' );
INSERT INTO `labels` VALUES ('1','5083','One of a kind trendy lip-gloss accessory! A charm decorated with Anna’s signature rose motif and comes with a mirror inside so you can touch up, jazz up your lips anywhere in any styles.' );
INSERT INTO `labels` VALUES ('1','84','SK-Ⅱ UV Lotion' );
INSERT INTO `labels` VALUES ('1','5084','UV protection with whitening care. Whitens spots and freckles and improves to clear and translucent skin. Protects from strong UV rays and improves to bright skin without being oily.' );
INSERT INTO `labels` VALUES ('1','85','La Prairie Caviar Eye' );
