INSERT INTO `labels` VALUES ('1','87','Diorsnow UV BB Cream' );
INSERT INTO `labels` VALUES ('1','5087','With its exclusive triple protection and its new-generation corrective pigments, the BB Cream immediately enhances the radiance of the complexion and reveals incredibly translucent skin.' );
INSERT INTO `labels` VALUES ('1','88','Alex Herbal BB Cream' );
